Message AN1xPatch::getSystemDataMsg()
{
	Message result;

	result = { 0xF0, 0x43, 0x00, 0x5C, 0x00, 0x1C, 0x00, 0x00, 0x00 };
	
	for (auto value : s_system) result.push_back(value);

	addCheckSum(result);

	return result;
}

